import logo from './logo.svg';
import './App.css';
import {Component} from 'react'; 
import data from "./got.json"; 

class App extends Component {

  constructor() {
    super(); 
    this.state = {
      characters :[
      ]
    }
  }

  //runs after the component output has been rendered to the DOM
  componentDidMount() { 
    fetch(data)
    .then(data => this.setState({characters: data}));     
  }

  // method examines this.props, which are parameters passed down by the parent and this.state, which is internal to the component
  render() {
    return (
      <div className="App">
        {
          this.state.characters.map(character => <h1 key={character.id}> {character.name}</h1>)
          // keys help react identify which items have changed 
        }
    </div>
  );
  }
}
export default App;
